var FormValidation=function()
{
	var e=function()
	{
		var e=$("#form_alta_personal"),r=$(".alert-danger",e),i=$(".alert-success",e);
		e.validate
		({
			errorElement:"span",
			errorClass:"help-block help-block-error",
			focusInvalid:!1,
			ignore:"",
			messages:
			{
				select_multi:
				{
					maxlength:jQuery.validator.format("Max {0} items allowed for selection"),
					minlength:jQuery.validator.format("At least {0} items must be selected")
				},
				membership:
				{
					required:"Please select a Membership type"
				},
				service:
				{
					required:"Please select  at least 2 types of Service",
					minlength:jQuery.validator.format("Please select  at least {0} types of Service")
				}
			},
			rules:
			{
				PayrollNumber:
				{
					minlength:2,
					number:!0,
					required:!0
				},
				Boss:
				{
				},
				UserEmail:
				{
					minlength:2
				},
				DatepickerDateAdmission:
				{
					required:!0
				},
				Names:
				{
					minlength:2,
					required:!0
				},
				LastNames:
				{
					minlength:2,
					required:!0
				},
				Gender:
				{
					required:!0
				},
				DatepickerBirthdate:
				{
					required:!0
				},
				CivilStatus:
				{
					required:!0
				},
				NumChildren:
				{
					digits:!0,
					required:!0
				},
				Curp:
				{
					minlength:18,
					maxlength:18,
					required:!0
				},
				Rfc:
				{
					minlength:13,
					maxlength:13,
					required:!0
				},
				Nss:
				{
					minlength:11,
					maxlength:11,
					digits:!0,
					required:!0
				},
				Phone:
				{
					minlength:14,
					maxlength:14
				},
				CellPhone:
				{
					minlength:14,
					maxlength:14
				},
				email:
				{
					email:!0
				},
				Zip:
				{
					minlength:5,
					maxlength:5,
					digits:!0,
					required:!0
				},
				StreetNum:
				{
					minlength:2,
					required:!0
				},
				State:
				{
					required:!0
				},
				Municipality:
				{
					required:!0
				},
				City:
				{
					required:!0
				},
				Colony:
				{
					required:!0
				},
				Clabe:
				{
					minlength:18,
					maxlength:18,
					digits:!0
				},
				Bank:
				{
				},
				AccountNum:
				{
					minlength:5,
					maxlength:11
				}
			},
			invalidHandler:function(e,t)
			{
				i.hide(),
				r.show(),
				App.scrollTo(r,-200)
			},
			errorPlacement:function(e,r)
			{
				var i=$(r).parent(".input-icon").children("i");
				i.removeClass("fa-check").addClass("fa-warning"),
				i.attr("data-original-title",e.text()).tooltip({container:"body"})
			},
			highlight:function(e)
			{
				$(e).closest(".form-group").removeClass("has-success").addClass("has-error")
			},
			unhighlight:function(e)
			{
				$(e).closest(".form-group").removeClass("has-error")
			},
			success:function(e)
			{
				var i=$(r).parent(".input-icon").children("i");
				$(r).closest(".form-group").removeClass("has-error").addClass("has-success"),
				i.removeClass("fa-warning").addClass("fa-check")
			},
			submitHandler:function(e)
			{
				i.show(),
				r.hide()
				e[0].submit()
			}
		}),
		$(".select2me",e).change(function(){e.validate().element($(this))}),
		$(".date-picker").datepicker({rtl:App.isRTL(),autoclose:!0}),
		$(".date-picker .form-control").change(function(){e.validate().element($(this))}),
		$(".touchspin").TouchSpin({initval:0}),
		$(".bs-select").selectpicker({iconBase:"fa",tickIcon:"fa-check"})
	};
	return{init:function(){e()}}
}();
jQuery(document).ready(function(){FormValidation.init()});